import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-diretivas-estruturais',
  templateUrl: './diretivas-estruturais.component.html',
  styleUrls: ['./diretivas-estruturais.component.scss']
})
export class DiretivasEstruturaisComponent implements OnInit {

  public condition: boolean =true;
  public conditionClick: boolean =true;
  public list: Array<{nome: string, idade: number }> = [
    { nome: "Guilherme Mendes", idade: 25},
    { nome: "Laylon Gilson", idade: 25},
    { nome: "Jose Sarney", idade: 99 }
  ];

  public nome: string ='Guilherme';
  constructor(){
  }

  ngOnInit(): void {

    setInterval(() => {
      if (this.condition) {
        this.condition = false;
      } else {
        this.condition = true;
      }
    }, 2000)

  }

  public onClick(){
    if (this.conditionClick) {
      this.conditionClick = false;
    } else {
      this.conditionClick = true;
    }
  }

  public onClickAddList(){
    this.list.push({nome: "Victor Corleone", idade: 31})
  }

  public onClickEventList(event: number){

    this.list.splice(event, 1);
  }



}
<p *ngIf="condition; else elseBlock">True</p>

<ng-template #elseBlock>
<p>False</p>
</ng-template>

<hr>
<p *ngIf="conditionClick">
    True
</p>

<p *ngIf="!conditionClick">False</p>

<button (click)="onClick()">ClickCondition</button>

<hr>

<ul>
    <li *ngFor="let item of list; let i= index;" (click)="onClickEventList(i)">
    [ {{i}} ] - {{item.nome}} - {{item.idade}}
</li>
</ul>
<button (click)="onClickAddList()">add</button>

<hr>
<hr>

<input type="text" [(ngModel)]="nome">
<div [ngSwitch]="'nome'" >
    <p *ngSwitchCase="'Guilherme'">
        Guilherme
    </p>
    <p *ngSwitchCase="'Nay'">
        Sunas</p>

        <p *ngSwitchDefault="'Nay'">
            Sunas</p>
</div>
