  ngAfterViewInit(): void {
    console.log("NgAfterViewInit")
  }

  ngAfterViewChecked(): void {
    console.log("NgAfterViewChecked")
  }
  public destruir: boolean = true;

  public destruirComponent(){
    this.destruir=false;

  }

